import './App.css';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { useEffect, useState } from 'react';
import AddTodo from './componets/AddTodo';
import ViewTodo from './componets/ViewTodo';

function App() {
  const [todo, setTodo] = useState(() => {
    const saveTodo = localStorage.getItem('todo');
    return saveTodo ? JSON.parse(saveTodo) : [];
  });
  useEffect(() => {
    localStorage.setItem('todo', JSON.stringify(todo));
  }, [todo])
  console.log(todo)
  return (
    <>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<AddTodo todo={todo} setTodo={setTodo} />} />
          <Route path="/list" element={<ViewTodo todo={todo} setTodo={setTodo} />} />
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default App;
