import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { AppointmentContext } from '../Context/AppointmentContext';
import { Your_Session_Context } from '../Context/Your_Session_Contex';

function Your_Session({ setIsCalendarOpen, setActiveTab }) {
    const { myData, setMyData } = useContext(Your_Session_Context);
    const { id } = useContext(AppointmentContext);

    const handleYourSession = async (id) => {
        setActiveTab("session");
        setIsCalendarOpen(false);
        try {
            const response = await axios.get(`http://localhost:8000/api/appointment/getSessionDetails/${id}`, {
                withCredentials: true
            });
            console.log(response.data);
            setMyData(response.data); // Store the fetched data
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div className='w-full m-0'>
            {/* Dropdown to select a session */}
            <div className='bg-slate-200 rounded-lg border w-full flex justify-center items-center text-center p-4'>
                <p className='text-xl px-2'>Session:</p>
                <select className='px-4 py-2 rounded-md w-1/2'>
                    <option value="">Select Session</option>
                </select>
            </div>
            
            {/* Display sessions fetched from the API */}
            <div>
                {myData && myData.length > 0 ? (
                    <ul>
                        {myData.map((item) => (
                            <li key={item._id}>
                                <p>{item.date}</p>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No sessions available</p>
                )}
            </div>
        </div>
    );
}

export default Your_Session;
