import React, { useContext, useEffect, useState } from 'react';
import Sessions from './Sessions';
import Slider from '../Slider';
import axios from 'axios';
import { AppointmentContext } from './Context/AppointmentContext';

function Appointment() {
  const [isSliderOpen, setIsSliderOpen] = useState(false);
  // const [name, setName] = useState([]);
  // const [selectedSessionName, setSelectedSessionName] = useState(null);
  const [sessionDetails, setSessionDetails] = useState(null);  // Initialize as null
  const [score, setScore] = useState('');
  // const [last_session, setLast_session] = useState('');
  const [type, setType] = useState('');
  const [feedback, setFeedback] = useState('');

  const { selectedSessionName, setSelectedSessionName, last_session, setLast_session } = useContext(AppointmentContext);
  useEffect(() => {
    const session = async () => {
      const response = await axios.get('http://localhost:8000/api/appointment/getClassSessions', { withCredentials: true });
      setName(response.data);
    }
    session();
  }, []);

  const toggleSlider = () => {
    setIsSliderOpen(!isSliderOpen);
  };

  const sessionSelection = (sessionName) => {
    setSelectedSessionName(sessionName);
  };


  return (
    <div>
      <div>
        {/* {last_session && <span>{last_session}</span>} */}

        <Sessions toggleSlider={toggleSlider} name={name} sessionSelection={sessionSelection} />
        {/* <div>
          <p>Score: {score}</p>
          <p>Last Session: {last_session}</p>
          <p>Type: {type}</p>
          <p>Feedback: {feedback}</p>
        </div> */}
      </div>

      {isSliderOpen && (
        <Slider closeSlider={toggleSlider} selectedSessionName={selectedSessionName} />
      )}
    </div>
  );
}

export default Appointment;
