import React from 'react';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { DateCalendar } from '@mui/x-date-pickers/DateCalendar';
import dayjs from 'dayjs';

function Calendar({ selectDate, handleChange, bookedSlots }) {
    const renderDay = (day, selectedDates, pickersDayProps) => {
        const dateKey = day.format('YYYY-MM-DD');
        const isBooked = bookedSlots[dateKey]?.length > 0;

        return (
            <div
                {...pickersDayProps}
                style={{
                    backgroundColor: isBooked ? '#ffcccc' : '',
                    color: isBooked ? '#ff0000' : '',
                    borderRadius: '50%',
                    padding: '5px',
                }}
            >
                {day.date()}
            </div>
        );
    };

    return (
        <div className="flex justify-between w-full text-sm">
            <div>
                <LocalizationProvider dateAdapter={AdapterDayjs}>
                    <DateCalendar
                        value={selectDate}
                        onChange={handleChange}
                        renderDay={renderDay} // Highlight booked days
                    />
                </LocalizationProvider>
            </div>
        </div>
    );
}

export default Calendar;
