import React, { useContext, useState } from 'react';
import Calender from './Appointment/Calender';
import TimeSlots from './Appointment/TimeSlots';
import closeIcon from '../assets/close.png';
import { AppointmentContext } from './Appointment/Context/AppointmentContext';

const Slider = ({ closeSlider }) => {
  const [selectDate, setSelectDate] = useState(null);
  const [bookedSlots, setBookedSlots] = useState({});
  const [sessionBook, setSessionBook] = useState(null);
  const [activeTab, setActiveTab] = useState('book');

  const {
    selectedSessionName, date, setDate, time, setTime, setLast_session
  } = useContext(AppointmentContext);

  const dateKey = selectDate ? selectDate.format('YYYY-MM-DD') : null;
  const slotsBookedForDate = bookedSlots[dateKey]
  const [isCalendarOpen, setIsCalendarOpen] = useState(false);



  // Handles the selected date change in the calendar
  const handleChange = (date) => {
    setSelectDate(date);
    setDate(date.format('YYYY-MM-DD'));
    setLast_session(date.format('YYYY-MM-DD'));
  };

  // Handles booking a time slot and updates booked slots state
  const handleBookSlot = (timeSlot) => {
    if (selectDate) {
      const dateKey = selectDate.format('YYYY-MM-DD');
      const currentBookings = bookedSlots[dateKey] || [];
      if (currentBookings.length < 2) { // Limit to two slots per date
        setTime(timeSlot)
        setBookedSlots((prev) => ({
          ...prev,
          [dateKey]: [timeSlot],
        }));
      }
    }
  };

  // Handles booking confirmation for a session and posts data to the backend
  const handleBook = async () => {
    setSessionBook({ date: selectDate.format('DD/MM/YYYY'), slots: slotsBookedForDate });

  };

  // Handler to open the calendar for selecting date and time slots
  const handleBookSessionClick = () => {
    setActiveTab('book');
    setIsCalendarOpen(true);
  };
  
  const handleYourSessionClick = () => {
    setActiveTab('session');
    setIsCalendarOpen(false);

  }

  // console.log(score, last_session, type, feedback)
  return (
    <div className="fixed top-0 right-0 h-full w-1/2 bg-gray-100 shadow-lg transform translate-x-0 transition-transform overflow-y-auto">
      {/* Header with Close Button */}
      <div>
        <div className="px-3 pt-3 rounded-lg flex justify-start text-slate-400">
          <button className="font-bold" onClick={closeSlider}>
            <img src={closeIcon} className='w-5' alt="Close" />
          </button>
          <p className="px-2 font-semibold text-sm">Mock Session</p>
        </div>
        <p className="border-b pb-3 text-slate-700 m-2 px-8 text-xl">{selectedSessionName}</p>
      </div>

      {/* Section to book or view session */}
      <div className="uppercase flex justify-around py-2 border-b-2 text-sm font-medium">
        <p onClick={handleBookSessionClick}
          className={`cursor-pointer ${activeTab === 'book' ? 'text-blue-500 border-b-2 border-blue-500' : ''}`}>
          Book Session
        </p>
        <p onClick={handleYourSessionClick}
          className={`cursor-pointer ${activeTab === 'session' ? 'text-blue-500 border-b-2 border-blue-500' : ''}`}>
          Your Session
        </p>
      </div>

      {/* Calendar and time slot selection */}
      <div className="px-6 mt-3">
        {!isCalendarOpen ? (
          <p className='flex justify-center items-center text-center h-screen text-slate-400'>No Sessions Taken</p>
        ) : (
          <div className="flex justify-between">
            <Calender selectDate={selectDate} handleChange={handleChange} />
            {selectDate && (
              <div>
                <div className='mb-3 bg-slate-200 rounded-lg w-1/2 mx-auto flex items-center justify-center text-center py-2'>
                  <h3>Select time-slot</h3>
                </div>
                <TimeSlots
                  slotsBookedForDate={slotsBookedForDate}
                  onBookSlot={handleBookSlot}
                  handleBook={handleBook}
                />
              </div>
            )}
          </div>
        )}
      </div>

      {/* Display session booking info */}
      <div className="flex justify-around mb-4">
        <div>
          {sessionBook ? (
            <div>
              <h2 className='text-[12px] text-slate-500 font-medium'>Session Booked for</h2>
              <h2>{date}, {time}</h2>
            </div>
          ) : (
            <h2>Available Slots for Booking</h2>
          )}
        </div>

        {/* Book session button */}
        <div className='flex justify-between'>
          <div>
            <button
              className='text-white p-2 border bg-blue-400 rounded-xl'
              onClick={handleBook}
            >
              Book Session
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Slider;
