import React, { useEffect, useState } from 'react';
import Sessions from './Sessions';
import Slider from '../Slider';
import axios from 'axios';

function Appointment() {
  const [isSliderOpen, setIsSliderOpen] = useState(false);
  const [name, setName] = useState([]);
  const [selectedSessionName, setSelectedSessionName] = useState(null);
  const [sessionDetails, setSessionDetails] = useState(null);  // Initialize as null
  const [score, setScore] = useState('');
  const [last_session, setLast_session] = useState('');
  const [type, setType] = useState('');
  const [feedback, setFeedback] = useState('');

  useEffect(() => {
    const session = async () => {
      const response = await axios.get('http://localhost:8000/api/appointment/getClassSessions', { withCredentials: true });
      setName(response.data);
    }
    session();
  }, []);

  const toggleSlider = () => {
    setIsSliderOpen(!isSliderOpen);
  };

  const sessionSelection = (sessionName) => {
    setSelectedSessionName(sessionName);
  };

  const sessionDetail = (sessionName) => {
    setSessionDetails(sessionName);
  };

  // Safely accessing the last element of sessionDetails.entries
  const getLastSession = () => {
    if (sessionDetails && sessionDetails.entries && Array.isArray(sessionDetails.entries) && sessionDetails.entries.length > 0) {
      return sessionDetails.entries[sessionDetails.entries.length - 1];
    }
    return null; // Return null if no entries are available
  };

  const lastSession = getLastSession();

  console.log(lastSession); // Logs the last session if available

  return (
    <div>
      <div>
        {/* Display last session's last_session value if lastSession is available */}
        {lastSession && <span>{lastSession.last_session}</span>}

        <Sessions toggleSlider={toggleSlider} name={name} sessionSelection={sessionSelection} sessionDetail={sessionDetail} />
      </div>

      {isSliderOpen && (
        <Slider closeSlider={toggleSlider} selectedSessionName={selectedSessionName} />
      )}
    </div>
  );
}

export default Appointment;
