import React, { useState } from 'react';

function TimeSlots({ bookedSlots, selectedDate, onBookSlot, selectDate }) {
    const [sessionBook, setSessionBook] = useState(null);
    const timeSlots = [
        '7:00 AM', '8:00 AM', '9:00 AM', '10:00 AM', '11:00 AM',
        '12:00 PM', '1:00 PM', '2:00 PM', '3:00 PM', '4:00 PM',
        '5:00 PM', '6:00 PM', '7:00 PM'
    ];

    // Get the booked slots for the selected date
    const dateKey = selectedDate ? selectedDate.format('YYYY-MM-DD') : null;
    const slotsBookedForDate = bookedSlots[dateKey] || [];

    const handleBook = () => {
        setSessionBook({ date: selectDate.format("DD/MM/YYYY"), slots: slotsBookedForDate });
    };

    return (
        <div>
            <div className=' texts'>
                {timeSlots.map((slot, index) => (
                    <button
                        className='rounded-lg text-sm'
                        key={index}
                        onClick={() => onBookSlot(slot)}
                        disabled={slotsBookedForDate.includes(slot) || slotsBookedForDate.length >= 2}
                        style={{
                            fontSize: '12px',
                            margin: '5px',
                            padding: '7px',
                            backgroundColor: slotsBookedForDate.includes(slot) ? '#d3d3d3' : '#4789F4',
                            color: 'white',
                            cursor: slotsBookedForDate.includes(slot) || slotsBookedForDate.length >= 2 ? 'not-allowed' : 'pointer',
                        }}
                    >
                        {slot} {slotsBookedForDate.includes(slot)}
                    </button>
                ))}
            </div>
            <div className='mt-5 flex justify-between'>
                <div>
                    {sessionBook ? (
                        <div>
                            <h2>Session Booked for {sessionBook.date}</h2>
                            <p>Time Slots: {sessionBook.slots.join(', ')}</p>
                        </div>
                    ) : (
                        <h2>Available Slots for Booking</h2>
                    )}
                </div>
                <div>
                    <button
                        className='text-white p-2 border bg-blue-400 rounded-xl'
                        onClick={handleBook}
                        disabled={slotsBookedForDate.length === 0}
                    >
                        Book Session
                    </button>
                </div>
            </div>
        </div>
    );
}

export default TimeSlots;
