import React, { useEffect, useState } from 'react';
import Sessions from './Sessions';
import Slider from '../Slider';
import axios from 'axios';

function Appointment() {
  const [isSliderOpen, setIsSliderOpen] = useState(false);
  const [selectedSession, setSelectedSession] = useState('');
  const [name, setName] = useState([])

  useEffect(() => {
    const session = async () => {
      const response = await axios.get('http://localhost:8000/api/appointment/getClassSessions', { withCredentials: true });
      // console.log(response.data);
      setName(response.data);
    }
    session();
  }, [])

  const sessionSelection = (selectedSession) => {
    console.log("Selected Session:", selectedSession.name);
    setSelectedSession(selectedSession.name)
  };
  const toggleSlider = () => {
    setIsSliderOpen(!isSliderOpen);
  };

  return (
    <div>
      <div>
        <Sessions sessionSelection={sessionSelection} toggleSlider={toggleSlider} name={name} />
      </div>

      {isSliderOpen && (
        <Slider closeSlider={toggleSlider} selectedSession={selectedSession} />
      )}
    </div>
  );
}

export default Appointment;
