import React, { useState } from 'react';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { DateCalendar } from '@mui/x-date-pickers/DateCalendar';
import dayjs from 'dayjs';

function Calendar() {
    const [selectDate, setSelectDate] = useState(null);
    const [bookedSlots, setBookedSlots] = useState({}); // Track booked slots by date

    const handleChange = (date) => {
        setSelectDate(date);
        console.log("Selected Date:", date ? date.format('YYYY-MM-DD') : null);
    };

    const handleBookSlot = (timeSlot) => {
        if (selectDate) {
            const dateKey = selectDate.format('YYYY-MM-DD');
            setBookedSlots(prev => ({
                ...prev,
                [dateKey]: timeSlot
            }));
        }
    };

    return (
        <div className='flex justify-between w-full'>
            <div>
                <LocalizationProvider dateAdapter={AdapterDayjs}>
                    <DateCalendar value={selectDate} onChange={handleChange} />
                </LocalizationProvider>
            </div>
            <div>
                {selectDate && (
                    <div>
                        <h3 className='pb-5'>
                            Available Time Slots for {selectDate.format('YYYY-MM-DD')}
                        </h3>
                        <TimeSlots
                            bookedSlots={bookedSlots}
                            selectedDate={selectDate}
                            onBookSlot={handleBookSlot}
                        />
                    </div>
                )}
            </div>
        </div>
    );
}

function TimeSlots({ bookedSlots, selectedDate, onBookSlot }) {
    const timeSlots = [
        '7:00 AM', '8:00 AM', '9:00 AM', '10:00 AM', '11:00 AM',
        '12:00 PM', '1:00 PM', '2:00 PM', '3:00 PM', '4:00 PM',
        '5:00 PM', '6:00 PM', '7:00 PM'
    ];

    const dateKey = selectedDate ? selectedDate.format('YYYY-MM-DD') : null;
    const isDateBooked = dateKey && bookedSlots[dateKey];

    return (
        <div>
            {timeSlots.map((slot, index) => (
                <button
                    className='rounded-lg text-sm'
                    key={index}
                    onClick={() => onBookSlot(slot)}
                    disabled={isDateBooked}
                    style={{
                        margin: '5px',
                        padding: '10px',
                        backgroundColor: isDateBooked ? '#d3d3d3' : '#4789F4',
                        color: 'white',
                        cursor: isDateBooked ? 'not-allowed' : 'pointer',
                    }}
                >
                    {slot} {isDateBooked && slot === bookedSlots[dateKey] ? '(Booked)' : ''}
                </button>
            ))}
            {isDateBooked && (
                <p style={{ color: 'red', fontWeight: 'bold', marginTop: '10px' }}>
                    This day is fully booked.
                </p>
            )}
        </div>
    );
}

export default Calendar;
