import React, { useState } from 'react';

const Slider = () => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleSlider = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className="relative">
            {/* Button to open the slider */}
            <button
                onClick={toggleSlider}
                className="px-4 py-2 bg-blue-500 text-white rounded"
            >
                Open Slider
            </button>

            {/* Slider */}
            <div
                className={`fixed top-0 right-0 h-full bg-gray-100 shadow-lg transition-transform transform ${isOpen ? 'translate-x-0' : 'translate-x-full'
                    } w-1/2`}
            >
                {/* Close button */}
                <button
                    onClick={toggleSlider}
                    className="absolute top-4 right-4 px-3 py-1 bg-red-500 text-white rounded"
                >
                    Close
                </button>
                <div className="p-6">
                    <h2 className="text-2xl font-semibold">Slider Content</h2>
                    <p className="mt-4">
                        This is a sliding panel that opens to half of the screen.
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Slider;
