import React from 'react';

const Slider = ({ closeSlider }) => {
  return (
    <div className="fixed top-0 right-0 h-full w-1/2 bg-gray-100 shadow-lg transform translate-x-0 transition-transform">
      {/* Close button */}
      <button
        onClick={closeSlider}
        className="absolute top-4 right-4 px-3 py-1 bg-red-500 text-white rounded"
      >
        Close
      </button>
      <div className="p-6">
        <h2 className="text-2xl font-semibold">Slider Content</h2>
        <p className="mt-4">
          This is a sliding panel that opens to half of the screen.
        </p>
      </div>
    </div>
  );
};

export default Slider;
