import React, { useContext, useEffect, useState } from 'react'
import { AppointmentContext } from '../Context/AppointmentContext';
import axios from 'axios';


function Your_Session() {
    const [items, setItems] = useState([]);
    const { setDate, setTime, setLast_session, date, time, name, id } = useContext(AppointmentContext);
    // console.log(id)
    useEffect(() => {
        const handleYourSessionClick = async () => {
            if (!id || !date) {
                console.log("Id and date not available")
            }
            try {
                const response = await axios.get(`http://localhost:8000/api/appointment/getSessionDetails/${id}?date=${date}`, {
                    withCredentials: true
                });
                setItems(response.data.entries);

            } catch (error) {
                console.log(error);
            }
        }
        handleYourSessionClick();
    }, [id, date])
    // console.log(items)
    return (
        <div>
            {
                items.map((item) => {
                    return (
                        <div className="flex flex-col text-gray-500">
                            <div className="bg-white rounded-lg border w-full p-5 items-center text-center">
                                {items && items.length > 0 ? (
                                    <>
                                        <h3 className="text-lg font-semibold text-gray-700 mb-4">Select Session:</h3>
                                        <select className="px-4 py-2 bg-gray-100 rounded-lg border focus:outline-none focus:border-blue-500 hover:bg-gray-200 transition w-full max-w-xs"
                                            >
                                            {items.map((item, index) => (
                                                <option key={index} value={item.date}>
                                                    {item.date}
                                                </option>
                                            ))}
                                        </select>
                                    </>
                                ) : (
                                    <p className="text-gray-400">No sessions available</p>
                                )}
                            </div>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default Your_Session
