import React, { useEffect, useState } from 'react';
import Calender from './Appointment/Calender';
import TimeSlots from './Appointment/TimeSlots';
import closeIcon from '../assets/close.png';
import axios from 'axios';

const Slider = ({ closeSlider, selectedSessionName }) => {
  const [selectDate, setSelectDate] = useState(null);
  const [bookedSlots, setBookedSlots] = useState({});
  const [sessionBook, setSessionBook] = useState(null);
  const dateKey = selectDate ? selectDate.format('YYYY-MM-DD') : null;
  // Post Method
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [score, setScore] = useState('');
  const [last_session, setLast_session] = useState('');
  const [type, settype] = useState('');
  const [feedback, setFeedback] = useState('');


  const slotsBookedForDate = bookedSlots[dateKey] || [];
  const handleBook = () => {
    setSessionBook({ date: selectDate.format("DD/MM/YYYY"), slots: slotsBookedForDate });
  };
  const handleChange = (date) => {
    setSelectDate(date);
    console.log("Selected Date:", date ? date.format('YYYY-MM-DD') : null);
    setDate(date.format('YYYY-MM-DD'));
  };
  const handleBookSlot = (timeSlot) => {
    if (selectDate) {
      const dateKey = selectDate.format('YYYY-MM-DD');
      const currentBookings = bookedSlots[dateKey] || [];
      if (currentBookings.length < 2) {
        setBookedSlots(prev => ({
          ...prev,
          [dateKey]: [timeSlot]
        }));
      }
    }
  };
  const [isCalendarOpen, setIsCalendarOpen] = useState(false);
  const handleBookSessionClick = () => {
    setIsCalendarOpen(true);
  };

  // Data store Mongodb
  useEffect(() => {
    const sliderData = async () => {
      const response = await axios.post('http://localhost:8000/api/appointment/addClass',
        {
          date,
          time,
          score,
          last_session,
          type,
          feedback,
        },
        {
          withCredentials: true
        }
      )
      console.log(response.data)
    }
    sliderData();
  }, [])
  console.log(slotsBookedForDate)
  setTime(slotsBookedForDate[0])
  return (
    <div className="fixed top-0 right-0 h-full w-1/2 bg-gray-100 shadow-lg transform translate-x-0 transition-transform overflow-y-auto">
      <div>
        <div className="p-3 shadow-lg rounded-lg flex justify-start gap-5 text-slate-400">
          <button className="font-bold" onClick={closeSlider}>
            <img src={closeIcon} className=' w-5' alt="Close" />
          </button>
          <p className="px-4 font-semibold text-sm">Mock Session</p>
        </div>
        <p className="border-b pb-3 text-slate-500 m-2 font-semibold px-3">{selectedSessionName}</p>
      </div>
      <div className="uppercase flex justify-between px-5 py-5 border-b-2 font-semibold">
        <p onClick={handleBookSessionClick} className="cursor-pointer text-blue-500">Book Session</p>
        <p>Your Session</p>
      </div>

      <div className="px-6 mt-3">
        {!isCalendarOpen ? (
          <p className=' flex justify-center items-center text-center h-screen text-slate-400'>No Sessions Taken</p>
        ) : (
          <div className=" flex justify-between">
            <Calender selectDate={selectDate} handleChange={handleChange} />
            {selectDate && (
              <div>
                <div className=' mb-3 bg-slate-200 rounded-lg w-1/2 mx-auto flex items-center justify-center text-center py-2'>
                  <h3>
                    Select time-slot
                  </h3>
                </div>
                <TimeSlots
                  slotsBookedForDate={bookedSlots[selectDate.format('YYYY-MM-DD')] || []}
                  onBookSlot={handleBookSlot}
                  handleBook={handleBook}
                />
              </div>
            )}
          </div>
        )}
      </div>

      <div className="flex justify-around mb-4">
        <div>
          {sessionBook ? (
            <div>
              <h2 className=' text-[12px] text-slate-500 font-medium'>Session Booked for</h2>
              <h2> {sessionBook.date}, {sessionBook.slots.join(', ')}</h2>
            </div>
          ) : (
            <h2>Available Slots for Booking</h2>
          )}
        </div>

        <div className='flex justify-between'>
          <div>
            <button
              className='text-white p-2 border bg-blue-400 rounded-xl'
              onClick={handleBook}
              disabled={slotsBookedForDate.length === 0}
            >
              Book Session
            </button>
          </div>
        </div>
      </div>

    </div>
  );
};

export default Slider;
