  };

  const toggleSlider = () => {
    setIsSliderOpen(!isSliderOpen);
  };

  return (
    <div>
      <div>
        <Sessions onClick={toggleSlider} >
          
        </Sessions>
      </div>

      <button  className="px-4 py-2 bg-blue-500 text-white rounded mt-4">
        Open Slider
      </button>

      {isSliderOpen && (
        <Slider closeSlider={toggleSlider} />
