import React from 'react';

const Slider = ({ closeSlider, sessionSelection }) => {
  console.log("Selected Session:", sessionSelection.name);
  return (
    <div className="fixed top-0 right-0 h-full w-1/2 bg-gray-100 shadow-lg transform translate-x-0 transition-transform">
      <div className=" p-3 shadow-lg rounded-lg flex justify-start gap-5 text-slate-400">
        <button className=' font-bold ' onClick={closeSlider}> X </button>
        <p className=' px-4 font-semibold'>Mock Session</p>
      </div>

      <div className="p-6">
        <h2 className="text-2xl font-semibold">Slider Content</h2>
        <p className="mt-4">
          This is a sliding panel that opens to half of the screen.
        </p>
      </div>
    </div>
  );
};

export default Slider;
