import React, { useState } from 'react';

function ViewTodo({ todo, setTodo }) {
    const [isEdit, setIsEdit] = useState(null);
    const [editTask, setEditTask] = useState("")

    const handleDelete = (index) => {
        const deleteTodo = todo.filter((item, i) => index !== i);
        setTodo(deleteTodo)
    }
    const handleEdit = (index) => {
        setIsEdit(index);
        setEditTask(todo[index]);
    }
    const handleSave = () => {
        setTodo(editTask)
    }

    return (
        <div className=' grid grid-cols-3 p-5'>
            {
                todo.map((item, index) => {
                    return (
                        <div>
                            {isEdit === index ?
                                (<div>
                                    <input
                                        type="text"
                                        value={editTask.name}
                                        onChange={(e) => setEditTask({ ...editTask, name: e.target.value })}
                                        placeholder="Name"
                                    />
                                    <input
                                        type="email"
                                        value={editTask.email}
                                        onChange={(e) => setEditTask({ ...editTask, email: e.target.value })}
                                        placeholder="Email"
                                    />
                                    <textarea
                                        value={editTask.task}
                                        onChange={(e) => setEditTask({ ...editTask, task: e.target.value })}
                                        placeholder="Task"
                                    />
                                    <button onClick={() => handleSave(index)}>Save</button>
                                    <button onClick={() => setEditTask(null)}>Cancel</button>
                                </div>
                                ) : (
                                    <div key={index} className=' border shadow-md p-5 m-2'>
                                        <h2>{item.name}</h2>
                                        <p>{item.email}</p>
                                        <p>{item.task}</p>
                                        <div className=' flex justify-around pt-4'>
                                            <button onClick={() => handleDelete(index)} className=' bg-red-400 px-4 py-2 border rounded-md shadow-md'>Delete</button>
                                            <button onClick={() => handleEdit(index)} className=' bg-yellow-400 px-4 py-2 border rounded-md shadow-md'>Edit</button>
                                        </div>
                                    </div>
                                )}
                        </div>
                    )
                })
            }
        </div>
    );
}

export default ViewTodo;
