import React, { useState } from 'react';
import Calender from './Appointment/Calender';
import TimeSlots from './Appointment/TimeSlots';

const Slider = ({ closeSlider, selectedSession }) => {

  const [selectDate, setSelectDate] = useState(null);
  const [bookedSlots, setBookedSlots] = useState({});
  const [sessionBook, setSessionBook] = useState(null);


  const dateKey = selectDate ? selectDate.format('YYYY-MM-DD') : null;
  const slotsBookedForDate = bookedSlots[dateKey] || [];

  const handleBook = () => {
    setSessionBook({ date: selectDate.format("DD/MM/YYYY"), slots: slotsBookedForDate });
  };


  const handleChange = (date) => {
    setSelectDate(date);
    console.log("Selected Date:", date ? date.format('YYYY-MM-DD') : null);
  };

  const handleBookSlot = (timeSlot) => {
    if (selectDate) {
      const dateKey = selectDate.format('YYYY-MM-DD');
      const currentBookings = bookedSlots[dateKey] || [];

      if (currentBookings.length < 2) {
        setBookedSlots(prev => ({
          ...prev,
          [dateKey]: [timeSlot]
        }));
      }
    }
  };

  // useEffect(async () => {
  //   const response = await axios.post('http://localhost:8000/api/appointment/getClassSessions', {

  //   })
  // }, [])

  const [isCalendarOpen, setIsCalendarOpen] = useState(false);
  console.log(selectedSession)
  const handleBookSessionClick = () => {
    setIsCalendarOpen(true);
  };

  return (
    <div className="fixed top-0 right-0 h-full w-1/2 bg-gray-100 shadow-lg transform translate-x-0 transition-transform">
      <div>
        <div className="p-3 shadow-lg rounded-lg flex justify-start gap-5 text-slate-400">
          <button className="font-bold" onClick={closeSlider}> X </button>
          <p className="px-4 font-normal text-sm">Mock Session</p>

        </div>
        <p className="border-b pb-3 text-slate-500 m-2 font-semibold">{selectedSession}</p>
      </div>
      <>

        <div className="uppercase flex justify-between px-5 py-5 border-b-2 font-semibold">
          <p onClick={handleBookSessionClick} className="cursor-pointer text-blue-500">Book Session</p>
          <p>Your Session</p>
        </div>
      </>
      <div className="p-6">
        {!isCalendarOpen ? (
          <>
            <p className=' flex justify-center items-center text-center h-screen text-slate-400'>No Sessions Taken</p>
          </>
        ) : (


          <div className=" flex justify-between">
            <Calender selectDate={selectDate} handleChange={handleChange} />
            {selectDate && (
              <div>

                <div className=' mb-3 bg-slate-200 rounded-lg w-1/2 mx-auto flex items-center justify-center text-center py-2'>
                  <h3>
                    Select time-slot
                  </h3>
                </div>
                <TimeSlots/>
              </div>
            )}

          </div>

        )}
      </div>
      <div>
        {sessionBook ? (
          <div>
            <h2>Session Booked for {sessionBook.date}</h2>
            <p>Time Slots: {sessionBook.slots.join(', ')}</p>
          </div>
        ) : (
          <h2>Available Slots for Booking</h2>
        )}
      </div>
    </div>
  );
};

export default Slider;
