import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import axios from 'axios';

const PrivateRoute = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(null);

    useEffect(() => {
        const checkAuth = async () => {
            try {
                const response = await axios.get('http://localhost:8000/api/appointment/dashboard', { withCredentials: true });
                console.log(response.data)
                setIsAuthenticated(true); 
            } catch (error) {
                setIsAuthenticated(false); 
            }
        };
        checkAuth();
    }, []);

    // Display loading indicator while checking authentication status
    if (isAuthenticated === null) {
        return <div>Loading...</div>; // You could use a spinner here for a better UI
    }

    // Redirect to /login if the user is not authenticated
    if (!isAuthenticated) {
        return <Navigate to="/login" replace />;
    }

    return children;
};

export default PrivateRoute;
