import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [task, setTask] = useState('');
  const [todo, setTodo] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const TodoTask = setTodo([{ name, email, task }])
    console.log(TodoTask);
    console.log(todo)
  }

  return (
    <div className=''>
      <div className=''>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="name">Name</label>
            <input type="text" placeholder='Please enter your name' name='name' value={name} onChange={(e) => setName(e.target.value)} />
          </div>
          <div>
            <label htmlFor="email">Email Id</label>
            <input type="text" placeholder='Please enter your email' name='email' value={email} onChange={(e) => setEmail(e.target.value)} />
          </div>
          <div>
            <label htmlFor="name">Name</label>
            <textarea placeholder='Please enter your Tasks ..........' name='email' value={task} onChange={(e) => setTask(e.target.value)} />
          </div>
          <button>Add Todo</button>
        </form>
      </div>
    </div>
  );
}

export default App;
