import React, { useContext, useState } from 'react'
import axios from 'axios';
import { AppointmentContext } from '../Context/AppointmentContext';


function Your_Session({ activeTab, setIsCalendarOpen, setActiveTab }) {
    const [isData, setIsData] = useState(false);
    const {
        id
    } = useContext(AppointmentContext);


    const handleYourSessionClick = async (id) => {
        setActiveTab("session")
        setIsCalendarOpen(false)
        try {
            const response = await axios.get(`http://localhost:8000/api/appointment/getSessionDetails/${id}`, {
                withCredentials: true
            })
            console.log(response.data)
        } catch (error) {
            console.log(error)
        }

    }
    return (
        <div>
            <div>
                <p onClick={() => handleYourSessionClick(id)}
                    className={`cursor-pointer ${activeTab === 'session' ? 'text-blue-500 border-b-2' : ''}`}>
                    Your Session
                </p>
            </div>

            <div className="px-6 mt-3">
                {!isData ? (
                    <p className='flex justify-center items-center text-center h-screen text-slate-400'>No Sessions Taken</p>
                ) : (
                    <div className="flex justify-between">
                        <Calender selectDate={selectDate} handleChange={handleChange} />
                        {selectDate && (
                            <div>
                                <div className='mb-3 bg-slate-200 rounded-lg w-1/2 mx-auto flex items-center justify-center text-center py-2'>
                                    <h3>Select time-slot</h3>
                                </div>
                                <TimeSlots
                                    slotsBookedForDate={slotsBookedForDate}
                                    onBookSlot={handleBookSlot}
                                    handleBook={handleBook}
                                />
                            </div>
                        )}
                    </div>
                )}
            </div>
        </div>
    )
}

export default Your_Session
