import React, { useContext, useEffect, useState } from 'react';
import Calender from './Appointment/Calender';
import TimeSlots from './Appointment/TimeSlots';
import closeIcon from '../assets/close.png';
import { AppointmentContext } from './Appointment/Context/AppointmentContext';
import axios from 'axios';
import moment from 'moment';
import debounce from 'lodash/debounce';


const Slider = ({ closeSlider }) => {
  const [selectDate, setSelectDate] = useState(null);
  const [bookedSlots, setBookedSlots] = useState({});
  const [sessionBook, setSessionBook] = useState(null);
  const [activeTab, setActiveTab] = useState('session');
  const [yourSession, setYourSession] = useState([]);
  const dateKey = selectDate ? selectDate.format('YYYY-MM-DD') : null;
  const slotsBookedForDate = bookedSlots[dateKey];
  const [isCalendarOpen, setIsCalendarOpen] = useState(false);

  const {
    selectedSessionName, date, setDate, time, setTime, setLast_session, id
  } = useContext(AppointmentContext);

  // Handles the selected date change in the calendar 
  const handleChange = (date) => {
    setSelectDate(date);
    setDate(date.format('YYYY-MM-DD'));
    setLast_session(date.format('YYYY-MM-DD'));
  };

  // Handles booking a time slot and updates booked slots state
  const handleBookSlot = (timeSlot) => {
    if (selectDate) {
      const dateKey = selectDate.format('YYYY-MM-DD');
      const currentBookings = bookedSlots[dateKey] || [];
      if (currentBookings.length < 2) { // Limit to two slots per date
        setTime(timeSlot);
        setBookedSlots((prev) => ({
          ...prev,
          [dateKey]: [timeSlot],
        }));
      }
    }
  };

  // Handles booking confirmation for a session and posts data to the backend
  const handleBook = async () => {
    setSessionBook({ date: selectDate.format('DD/MM/YYYY'), slots: slotsBookedForDate });
  };

  // Handler to open the calendar for selecting date and time slots
  const handleBookSessionClick = () => {
    setActiveTab('book');
    setIsCalendarOpen(true);
  };

  const handleDateSelection = async (sessionId, sessionDate) => {
    setActiveTab("session");
    setIsCalendarOpen(false);

    try {
      const response = await axios.get(
        `http://localhost:8000/api/appointment/getSessionDetails/${sessionId}?date=${sessionDate}`,
        { withCredentials: true }
      );
      const sessions = response.data.entries;
      console.log("Fetched sessions:", sessions); // Log fetched data

      setYourSession(sessions); // Store fetched data

      if (sessions && sessions.length > 0) {
        const lastDate = sessions[sessions.length - 1].date;
        if (lastDate !== date) {
          setDate(lastDate);
          setSelectDate(moment(lastDate, 'YYYY-MM-DD'));
        }
      }
    } catch (error) {
      console.error("Error fetching session details:", error);
    }
  };

  // useEffect(() => {
  //   if (id && date) {
  //     handleDateSelection(id, date);
  //   }
  // }, [id, date]); // Include date in the dependency array




  return (
    <div className="fixed top-0 right-0 h-full w-1/2 bg-gray-100 shadow-lg transform translate-x-0 transition-transform overflow-y-auto">
      {/* Header with Close Button */}
      <div>
        <div className="px-3 pt-3 rounded-lg flex justify-start text-slate-400">
          <button className="font-bold" onClick={closeSlider}>
            <img src={closeIcon} className='w-5' alt="Close" />
          </button>
          <p className="px-2 font-semibold text-sm">Mock Session</p>
        </div>
        <p className="border-b pb-3 text-slate-700 m-2 px-8 text-xl">{selectedSessionName}</p>
      </div>

      {/* Section to book or view session */}
      <div className="uppercase flex justify-around py-2 border-b-2 text-sm font-medium">
        <p onClick={handleBookSessionClick}
          className={`cursor-pointer ${activeTab === 'book' ? 'text-blue-500 border-b-2' : ''}`}>
          Book Session
        </p>
        <p onClick={() => handleDateSelection(id, date)}
          className={`cursor-pointer ${activeTab === 'session' ? 'text-blue-500 border-b-2' : ''}`}>
          Your Session
        </p>
      </div>

      {/* Calendar and time slot selection */}
      <div className="p-6 mt-6 bg-gray-50 rounded-lg mb-10">
        {!isCalendarOpen ? (
          <div className="flex flex-col text-gray-500">
            <div className="bg-white rounded-lg border w-full p-5 items-center text-center">
              {yourSession && yourSession.length > 0 ? (
                <>
                  <h3 className="text-lg font-semibold text-gray-700 mb-4">Select Session:</h3>
                  <select className="px-4 py-2 bg-gray-100 rounded-lg border focus:outline-none focus:border-blue-500 hover:bg-gray-200 transition w-full max-w-xs"
                    onChange={handleDateSelection}>
                    {yourSession.map((item, index) => (
                      <option key={index} value={item.date}>
                        {item.date}
                      </option>
                    ))}
                  </select>
                </>
              ) : (
                <p className="text-gray-400">No sessions available</p>
              )}
            </div>

            <div className="bg-white rounded-lg border w-full p-5 my-2">
              <div className="border-b text-gray-500 text-sm mb-1">
                <h2>Session List</h2>
              </div>
              <div className=' items-center mt-2'>
                {yourSession.map((item, index) => (
                  <div key={index}>
                    {item.time}, {item.date}
                  </div>
                ))}
              </div>
              <div className="border-t pt-2 mt-2 text-gray-500 text-sm">
                <p>No Recordings Available</p>
              </div>
            </div>

            <div className="bg-white rounded-lg border w-full p-5 my-2">
              <div className="border-b  text-gray-500 text-sm mb-1">
                <h2>Section Wise Analysis</h2>
              </div>
              <div className=' items-center mt-2'>
                {yourSession.map((item, index) => (
                  <div key={index}>
                    {
                      item.score ?
                        <div className=' flex justify-between'>
                          <div>Score:</div>
                          <div className=' float-end text-green-600 text-5xl px-3'>{item.score}</div>
                        </div>
                        :
                        "Score not available"
                    }
                  </div>
                ))}
              </div>
            </div>

          </div>
        ) : (
          <div className="flex flex-col md:flex-row justify-between items-start space-y-6 md:space-y-0 md:space-x-6">
            <div className="w-full md:w-1/2">
              <Calender selectDate={selectDate} handleChange={handleChange} />
            </div>
            {selectDate && (
              <div className="w-full md:w-1/2">
                <div className="mb-4 bg-blue-100 rounded-lg px-4 py-2 text-center font-semibold text-blue-700">
                  Select time-slot
                </div>
                <TimeSlots
                  slotsBookedForDate={slotsBookedForDate}
                  onBookSlot={handleBookSlot}
                  handleBook={handleBook}
                />
              </div>
            )}
          </div>
        )}
      </div>

      {activeTab === 'book' ? (
        <div className="flex justify-around mb-4">
          <div>
            {sessionBook ? (
              <div>
                <h2 className='text-[12px] text-slate-500 font-medium'>Session Booked for</h2>
                <h2>{date}, {time}</h2>
              </div>
            ) : (
              <h2>Available Slots for Booking</h2>
            )}
          </div>

          {/* Book session button */}
          <div className='flex justify-between'>
            <div>
              <button
                className='text-white p-2 border bg-blue-400 rounded-xl'
                onClick={handleBook}
              >
                Book Session
              </button>
            </div>
          </div>
        </div>
      ) : (
        <p className='text-center'>PLEASE BOOK SESSION</p>
      )}
    </div>
  );
};

export default Slider;
