import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import axios from 'axios';

const PrivateRoute = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(null);

    useEffect(() => {
        const checkAuth = async () => {
            try {
                const response = await axios.get('http://localhost:8000/api/appointment/checkAuth', { withCredentials: true });
                setIsAuthenticated(response.data.isAuthenticated); // Assume backend sends isAuthenticated as true/false
            } catch (error) {
                setIsAuthenticated(false); // If error, assume user is not authenticated
            }
        };

        checkAuth();
    }, []);

    // Loading state while checking auth
    if (isAuthenticated === null) {
        return <div>Loading...</div>;
    }

    // Redirect to /login if not authenticated
    if (!isAuthenticated) {
        return <Navigate to="/login" />;
    }

    // Render protected component if authenticated
    return children;
};

export default PrivateRoute;
