import React, { useState } from 'react'

function ViewTodo({ todo, setTodo }) {
    const [isEditted, setIsEddited] = useState(null);
    const [editData, setEditData] = useState([])
    const handleDelete = (index) => {
        const delteTask = todo.filter((item, i) => index !== i);
        setTodo(delteTask)
    }
    const handleEdit = (index) => {
        setIsEddited(index);
        setEditData(todo[index]);
    }

    const handleSave = (index) => {
        const updateData = [...todo];
        updateData[index] = editData;
        setTodo(updateData)
        setIsEddited(null);
    }

    return (
        <div>
            {todo.map((item, index) => {
                <div className=' grid grid-cols-3 p-5 '>
                    {
                        isEditted ?
                            (
                                <div className=' p-5 border shadow-lg rounded-md w-1/2'>
                                    <div className=' items-center text-center font-semibold text-xl uppercase'>
                                        <h2 >Add Todo</h2>
                                    </div>
                                    <div className=' py-2'>
                                        <label htmlFor="name">Name</label>
                                        <input type="text" placeholder='Please Enter name' className=' p-2 w-full border outline-none rounded-md' value={editData.name} onChange={(e) => setEditData({ ...editData.name, name: e.target.value })} id='name' />
                                    </div>
                                    <div className=' py-2'>
                                        <label htmlFor="email">Email</label>
                                        <input type="text" placeholder='Please Enter Email Id' className=' p-2 w-full border outline-none rounded-md' value={editData.email} onChange={(e) => setEditData({ ...editData.email, email: e.target.value })} id='email' />
                                    </div>
                                    <div className=' py-2'>
                                        <label htmlFor="date">Data</label>
                                        <input type="date" placeholder='Please Enter date' className=' p-2 w-full border outline-none rounded-md' value={editData.date} onChange={(e) => setEditData({ ...editData.date, date: e.target.value })} id='date' />
                                    </div>
                                    <div className=' py-2'>
                                        <label htmlFor="status">Status</label>
                                        <select className=' p-2 w-full border outline-none rounded-md' value={editData.status} onChange={(e) => setEditData({ ...editData.status, status: e.target.value })} id='status'>
                                            <option value="complete">Complete</option>
                                            <option value="uncomplete">Uncomplete</option>
                                        </select>
                                    </div>
                                    <div className=' flex justify-around pt-5'>
                                        <button onClick={(e) => handleSave(index)} className=' border px-4 py-2 rounded-lg shadow-md bg-orange-500-400'>Save</button>
                                        <button className=' border px-4 py-2 rounded-lg shadow-md bg-orange-500-400'>Cancel</button>
                                    </div>
                                </div>
                            )
                            :
                            (


                                <div key={index} className=' border p-5 m-3 rounded-md shadow-lg'>
                                    <h1>{item.name}</h1>
                                    <h1>{item.email}</h1>
                                    <h1>{item.date}</h1>
                                    <h1>{item.status}</h1>
                                    <div className=' flex justify-around pt-5'>
                                        <button onClick={() => handleDelete(index)} className=' border px-4 py-2 rounded-lg shadow-md bg-orange-500-400'>Delete</button>
                                        <button onClick={() => handleEdit(index)} className=' border px-4 py-2 rounded-lg shadow-md bg-orange-500-400'>Edit</button>
                                    </div>
                                </div>

                            )
                    }
                </div>
            })}
        </div>
    )
}
export default ViewTodo
