import React, { useState } from 'react';

function AddTodo({ todo, setTodo }) {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [task, setTask] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        
        // Check if all fields are filled before adding
        if (!name || !email || !task) {
            alert("All fields are required!");
            return;
        }

        const newTodo = { name, email, task };
        
        // Update the todo list
        setTodo([...todo, newTodo]);

        // Clear form inputs
        setName('');
        setEmail('');
        setTask('');
    };

    return (
        <div className="flex justify-center items-center h-screen">
            <div className="bg-white shadow-lg p-5 w-1/2 rounded-md">
                <form onSubmit={handleSubmit}>
                    <div className="pt-3">
                        <label htmlFor="name">Name</label>
                        <input
                            type="text"
                            className="w-full p-2 border outline-none rounded-md"
                            placeholder="Please enter your name"
                            name="name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                        />
                    </div>
                    <div className="pt-3">
                        <label htmlFor="email">Email Id</label>
                        <input
                            type="text"
                            className="w-full p-2 border outline-none rounded-md"
                            placeholder="Please enter your email"
                            name="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                    </div>
                    <div className="pt-3">
                        <label htmlFor="task">Task</label>
                        <textarea
                            className="w-full p-2 border outline-none rounded-md"
                            placeholder="Please enter your task..."
                            name="task"
                            value={task}
                            onChange={(e) => setTask(e.target.value)}
                        />
                    </div>
                    <button className="px-3 py-2 border bg-amber-400 rounded-lg mt-4">
                        Add Todo
                    </button>
                </form>
            </div>
        </div>
    );
}

export default AddTodo;
