import React, { useState } from 'react';
import Sessions from './Sessions';
import Slider from '../Slider';

function Appointment() {
  const [isSliderOpen, setIsSliderOpen] = useState(false);
  const [selectedSession, setSelectedSession] = useState('')
  const sessionSelection = (selectedSession) => {
    console.log("Selected Session:", selectedSession.name);
    setSelectedSession(selectedSession.name)
  };
  const toggleSlider = () => {
    setIsSliderOpen(!isSliderOpen);
  };

  return (
    <div>
      <div>
        <Sessions sessionSelection={sessionSelection} toggleSlider={toggleSlider} />
      </div>

      {isSliderOpen && (
        <Slider closeSlider={toggleSlider} selectedSession={selectedSession} />
      )}
    </div>
  );
}

export default Appointment;
