import React, { useEffect, useState } from 'react';
import Sessions from './Sessions';
import Slider from '../Slider';
import axios from 'axios';

function Appointment() {
  const [isSliderOpen, setIsSliderOpen] = useState(false);
  const [name, setName] = useState([]);
  const [selectedSessionName, setSelectedSessionName] = useState(null);
  const [sessionDetails, setSessionDetails] = useState('');
  const [score, setScore] = useState('');
  const [last_session, setLast_session] = useState('');
  const [type, setType] = useState('');
  const [feedback, setFeedback] = useState('');
  
  useEffect(() => {
    const fetchSessions = async () => {
      const response = await axios.get('http://localhost:8000/api/appointment/getClassSessions', { withCredentials: true });
      setName(response.data);
    }
    fetchSessions();
  }, []);

  const toggleSlider = () => {
    setIsSliderOpen(!isSliderOpen);
  };

  const sessionSelection = (sessionName) => {
    setSelectedSessionName(sessionName);
  };

  const sessionDetail = (sessionName) => {
    setSessionDetails(sessionName);
  };

  // Update individual fields from the last entry in sessionDetails
  useEffect(() => {
    if (sessionDetails?.entries && sessionDetails.entries.length > 0) {
      const lastEntry = sessionDetails.entries[sessionDetails.entries.length - 1];
      setScore(lastEntry.score || '');
      setLast_session(lastEntry.last_session || '');
      setType(lastEntry.type || '');
      setFeedback(lastEntry.feedback || '');
    }
    console.log(score)
    console.log(last_session)
  }, [sessionDetails]);

  return (
    <div>
      <div>
        {/* Display last session date if available */}
        {last_session && <span>{last_session}</span>}
        
        <Sessions toggleSlider={toggleSlider} name={name} sessionSelection={sessionSelection} sessionDetail={sessionDetail} />
        
        {/* Displaying the fields */}
        <div>
          <p>Score: {score}</p>
          <p>Last Session: {last_session}</p>
          <p>Type: {type}</p>
          <p>Feedback: {feedback}</p>
        </div>
      </div>

      {isSliderOpen && (
        <Slider closeSlider={toggleSlider} selectedSessionName={selectedSessionName} />
      )}
    </div>
  );
}

export default Appointment;
