import React, { useContext, useEffect, useState } from 'react';
import { AppointmentContext } from './Context/AppointmentContext';
import axios from 'axios';

function AppointmentForm() {
  const [task, setTask] = useState(null);  // Assuming one session object
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state
  const {
    selectedSessionName,
    date,
    score,
    time,
    type,
    feedback,
    setFeedback,
    setScore,
    id,
    last_session
  } = useContext(AppointmentContext);

  // Save data on changes to any relevant state
  useEffect(() => {
    const saveData = async () => {
      try {
        const response = await axios.put('http://localhost:8000/api/appointment/updateSession', {
          session_name: selectedSessionName,
          type: type,
          entries: [
            {
              _id: id,
              score: score,
              last_session: last_session,
              feedback: feedback,
              type: type,
              date: date,
              time: time
            }
          ]
        },
          { withCredentials: true }
        );
        console.log(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    saveData();
  }, [selectedSessionName, date, score, time, type, feedback, id, last_session]);  // Adding dependencies

  // Fetch session data based on sessionId
  useEffect(() => {
    const fetchData = async () => {
      if (!id) {
        console.error("sessionId is missing");
        return;
      }

      try {
        const response = await axios.get(
          `http://localhost:8000/api/appointment/getSessionDetails/${id}`,
          { withCredentials: true }
        );
        console.log(response.data);
        setTask(response.data);
      } catch (error) {
        setError("Failed to fetch session details");
        console.error("Failed to fetch session details:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]); // Adding sessionId as a dependency

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Handle form submission logic if any
  };

  const getSafeValue = (field) => {
    return field ? field : ""; // Return empty string if field is missing or empty
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <div>
        {task && (
          <div key={task._id}>
            <h2>{task.session_name}</h2>
          </div>
        )}
      </div>
      <div className='flex justify-center bg-white text-slate-700 mb-10'>
        <div className='p-5 border w-1/2 items-center shadow-md rounded-lg bg-white'>
          <div className='text-center text-2xl font-semibold uppercase py-3'>Register Form</div>
          <form onSubmit={handleSubmit} className='font-semibold'>
            <div className='py-2'>
              <label>Session Name:</label>
              <input
                type='text'
                className='w-full px-3 py-2 border outline-none rounded-md'
                readOnly
                value={selectedSessionName}
              />
            </div>
            <div className='py-2'>
              <label>Type:</label>
              <input
                type='text'
                className='w-full px-3 py-2 border outline-none rounded-md'
                readOnly
                value={type}
              />
            </div>
            <div className='py-2'>
              <div className='w-full px-3 py-2 border outline-none rounded-md'>
                Date: {date} | Time: {time}
              </div>
            </div>
            <div className='py-2'>
              <label>Score:</label>
              <input
                type='number'
                className='w-full px-3 py-2 border outline-none rounded-md'
                placeholder='Enter score'
                value={score}
                onChange={(e) => setScore(e.target.value)}
              />
            </div>
            <div className='py-2'>
              <label>Feedback:</label>
              <input
                type='text'
                className='w-full px-3 py-2 border outline-none rounded-md'
                placeholder='Enter feedback'
                value={feedback}
                onChange={(e) => setFeedback(e.target.value)}
              />
            </div>
            <div className='py-2'>
              <button type='submit' className='px-4 py-2 bg-blue-400 rounded-lg text-white'>
                Register
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default AppointmentForm;
