import React, { useState } from 'react';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { DateCalendar } from '@mui/x-date-pickers/DateCalendar';

function Calendar() {
    const [selectDate, setSelectDate] = useState(null);
    const [bookedSlots, setBookedSlots] = useState([]); // To track booked time slots

    const handleChange = (e) => {
        setSelectDate(e);
        console.log("Selected Date:", e);
    };

    const handleBookSlot = (timeSlot) => {
        // Disable the slot by adding it to booked slots
        setBookedSlots([...bookedSlots, timeSlot]);
    };

    return (
        <div className='grid grid-cols-[70%_30%] gap-4'>
            <div>
                <LocalizationProvider dateAdapter={AdapterDayjs}>
                    <DateCalendar value={selectDate} onChange={handleChange} />
                </LocalizationProvider>
            </div>
            <div>
                {selectDate && (
                    <div>
                        <h3>Available Time Slots for {selectDate.format('YYYY-MM-DD')}</h3>
                        <TimeSlots
                            bookedSlots={bookedSlots}
                            onBookSlot={handleBookSlot}
                        />
                    </div>
                )}
            </div>
        </div>
    );
}

function TimeSlots({ bookedSlots, onBookSlot }) {
    // Time slots from 7 AM to 7 PM
    const timeSlots = [
        '7:00 AM', '8:00 AM', '9:00 AM', '10:00 AM', '11:00 AM',
        '12:00 PM', '1:00 PM', '2:00 PM', '3:00 PM', '4:00 PM',
        '5:00 PM', '6:00 PM', '7:00 PM'
    ];

    return (
        <div>
            {timeSlots.map((slot, index) => (
                <button
                    key={index}
                    onClick={() => onBookSlot(slot)}
                    disabled={bookedSlots.includes(slot)} // Disable if already booked
                    style={{
                        margin: '5px',
                        padding: '10px',
                        backgroundColor: bookedSlots.includes(slot) ? '#d3d3d3' : '#4CAF50',
                        color: 'white',
                        cursor: bookedSlots.includes(slot) ? 'not-allowed' : 'pointer',
                    }}
                >
                    {slot} {bookedSlots.includes(slot) && '(Booked)'}
                </button>
            ))}
        </div>
    );
}

export default Calendar;
