import React, { useState } from 'react';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { DateCalendar } from '@mui/x-date-pickers/DateCalendar';

function Calendar() {
    const [selectDate, setSelectDate] = useState(null);
    const [bookedSlots, setBookedSlots] = useState({});

    const handleChange = (date) => {
        setSelectDate(date);
        console.log("Selected Date:", date ? date.format('YYYY-MM-DD') : null);
    };

    const handleBookSlot = (timeSlot) => {
        if (selectDate) {
            const dateKey = selectDate.format('YYYY-MM-DD');
            const currentBookings = bookedSlots[dateKey] || [];

            if (currentBookings.length < 2) {
                setBookedSlots(prev => ({
                    ...prev,
                    [dateKey]: [timeSlot]
                }));
            }
        }
    };

    return (
        <div className='flex justify-between w-full  text-sm'>
            <div>
                <LocalizationProvider dateAdapter={AdapterDayjs}>
                    <DateCalendar className='' value={selectDate} onChange={handleChange} />
                </LocalizationProvider>
            </div>
            <div>
                {selectDate && (
                    <div>
                        {/* <h3 className='pb-5'>
                            Available Time Slots for {selectDate.format('YYYY-MM-DD')}
                        </h3> */}
                        <div className='pb-5 bg-slate-300 rounded-lg w-1/2 mx-auto flex items-center justify-center text-center h-24'>
    <h3>
        Select time-slot
    </h3>
</div>

                        <h3></h3>
                        <TimeSlots
                            bookedSlots={bookedSlots}
                            selectedDate={selectDate}
                            onBookSlot={handleBookSlot}
                        />
                    </div>
                )}
            </div>
        </div>
    );
}

function TimeSlots({ bookedSlots, selectedDate, onBookSlot }) {
    const timeSlots = [
        '7:00 AM', '8:00 AM', '9:00 AM', '10:00 AM', '11:00 AM',
        '12:00 PM', '1:00 PM', '2:00 PM', '3:00 PM', '4:00 PM',
        '5:00 PM', '6:00 PM', '7:00 PM'
    ];

    // Get the booked slots for the selected date
    const dateKey = selectedDate ? selectedDate.format('YYYY-MM-DD') : null;
    const slotsBookedForDate = bookedSlots[dateKey] || [];

    return (
        <div>
            {timeSlots.map((slot, index) => (
                <button
                    className='rounded-lg text-sm'
                    key={index}
                    onClick={() => onBookSlot(slot)}
                    disabled={slotsBookedForDate.includes(slot) || slotsBookedForDate.length >= 2}
                    style={{
                        margin: '5px',
                        padding: '10px',
                        backgroundColor: slotsBookedForDate.includes(slot) ? '#d3d3d3' : '#4789F4',
                        color: 'white',
                        cursor: slotsBookedForDate.includes(slot) || slotsBookedForDate.length >= 2 ? 'not-allowed' : 'pointer',
                    }}
                >
                    {slot} {slotsBookedForDate.includes(slot) && '(Booked)'}
                </button>
            ))}
            {/* {slotsBookedForDate.length >= 2 && (
                <p style={{ color: 'red', fontWeight: 'bold', marginTop: '10px' }}>
                    Maximum of 2 bookings reached for this day.
                </p>
            )} */}
        </div>
    );
}

export default Calendar;
